customCss:
/* 1. Zentrierter Container */
.centered-box {
  max-width: 800px;
  margin-inline: auto;
  padding: 2rem;
  background: #f5f5f5;
  border-radius: 0.75rem;
}

/* 2. Prim√§rer Button */
.btn-primary {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  padding: 0.7rem 1.4rem;
  border-radius: 999px;
  border: none;
  background: linear-gradient(135deg, #2563eb, #1d4ed8);
  color: #fff;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 10px 20px rgba(15, 23, 42, 0.25);
  transition: transform 0.15s ease, box-shadow 0.15s ease, filter 0.15s ease;
}
.btn-primary:hover {
  transform: translateY(-1px);
  filter: brightness(1.06);
  box-shadow: 0 16px 30px rgba(15, 23, 42, 0.3);
}
.btn-primary:active {
  transform: translateY(0);
  box-shadow: 0 8px 16px rgba(15, 23, 42, 0.28);
}

/* 3. Card mit Shadow */
.card-elevated {
  border-radius: 1rem;
  background: #ffffff;
  padding: 1.5rem;
  box-shadow: 0 18px 45px rgba(15, 23, 42, 0.16);
  border: 1px solid rgba(148, 163, 184, 0.3);
}

/* 4. Flex Row Utilities */
.flex-row-center {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 1rem;
}

/* 5. Badge */
.badge-pill {
  display: inline-flex;
  align-items: center;
  padding: 0.2rem 0.75rem;
  border-radius: 999px;
  background: rgba(56, 189, 248, 0.12);
  color: #0369a1;
  font-size: 0.75rem;
  font-weight: 600;
  letter-spacing: 0.04em;
  text-transform: uppercase;
}

/* 6. Typo-Highlight */
.text-gradient {
  background: linear-gradient(135deg, #6366f1, #ec4899, #f97316);
  -webkit-background-clip: text;
  color: transparent;
}

/* 7. Simple Grid */
.grid-3 {
  display: grid;
  grid-template-columns: repeat(3, minmax(0, 1fr));
  gap: 1.25rem;
}

/* 8. Image Hover */
.img-zoom {
  display: block;
  border-radius: 0.75rem;
  overflow: hidden;
}
.img-zoom img {
  display: block;
  width: 100%;
  height: auto;
  transition: transform 0.3s ease;
}
.img-zoom:hover img {
  transform: scale(1.06);
}

/* 9. Subtile Border-Section */
.section-soft {
  padding: 2.5rem 2rem;
  border-radius: 1.25rem;
  border: 1px solid rgba(148, 163, 184, 0.35);
  background: radial-gradient(circle at top left, rgba(59, 130, 246, 0.06), transparent 55%),
              radial-gradient(circle at bottom right, rgba(236, 72, 153, 0.06), transparent 55%);
}

/* 10. Utility Spacing (Rem-Scale) */
.mt-4 { margin-top: 1rem; }
.mb-4 { margin-bottom: 1rem; }
.mx-auto { margin-left: auto; margin-right: auto; }
.p-4 { padding: 1rem; }
.px-6 { padding-left: 1.5rem; padding-right: 1.5rem; }
